import './App.css';

function App() {
  const data = [
    {
      image: "data:image/jpeg;base64,",
      product: "Apple iPhone 14Pro Max",
      price: '₹ 1,77,999',
      discount: '₹ 1,89,990',
      sale: "Sale"
    },
    {
      image: "data:image/jpeg;base64,",
      product: "Samsung Galxy Z Fold",
      price: '₹ 1,49,990'
    },
    {
      image: "data:image/jpeg;base64,",
      price: '₹ 29,999',
      product: "OnePlus Nord 2",
      sale: "Sale"

    },
    {
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRboxA3zKbY2SlG6UJpPOdzP2-bgDiOeayaIoW7ZzoG6Q&usqp=CAU&ec=48600112",
      product: "IQOO Neo 6",
      price: '₹ 29,999',
      sale: "Sale",
      discount: '₹ 34,999',
    },
    {
      image: "data:image/jpeg;base64,",
      product: "Realme GT Neo 3T",
      price: '₹ 29,999',
      discount: '₹ 35,599'
    },
    {
      image: "data:image/jpeg;base64,",
      product: "Redmi Note 12 pro+",
      price: '₹ 28,999',
      discount: '₹ 33,999'
    },
    {
      image: "data:image/jpeg;base64,",
      product: "LG G7+ ThinQ",
      price: '₹ 29,499',
      sale: "Sale",
      discount: '₹ 47,999'
    },
    {
      image: "data:image/jpeg;base64,",
      product: "OPPO Reno8T",
      price: '₹ 34,999'
    }

  ]
  const [count, setCount] = useState(0)
  return (
    <div className="App">
      <div className="cart">
        <Badge bg="success">
          CART {count}
        </Badge>
      </div>
      <div className='card-container'>
        {data.map((prod, idx) => <Cards
          idx={idx}
          prod={prod}
          count={count}
          setCount={setCount}
        />)}
      </div>
    </div>
  );
}

export default App;

function Cards({ prod, idx, setCount, count }) {
  const [show, setShow] = useState(false);

  function addToCart() {
    setShow(!show)
    setCount(count + 1)
  }

  function removeFromCart() {
    setShow(!show)
    setCount(count - 1)
  }

  return (
    <Card className='hov' key={idx} style={{ width: '17rem' }}>
      <Card.Text className={prod.sale}>{prod.sale}</Card.Text>
      <Card.Img className='img ' variant="top" src={prod.image} />
      <Card.Body>
        <Card.Title>{prod.product}</Card.Title>
        <Card.Text><s className='gr'>{prod.discount}</s>  {prod.price}</Card.Text>
        {!show ? <Button
          variant="primary"
          onClick={addToCart}
        >Add to Cart</Button> : ""}
        {show ? <Button
          variant="danger"
          onClick={removeFromCart}
        >Remove to Cart</Button> : ""}
      </Card.Body>
    </Card>
  )
}


